for (i = 0; i <= 5; ++i) {
  draw(rotate(60*i)*(foo),red);
	draw(rotate(60*i)*(((1,0) + dir(-60))--(1,0)--((1,0) + dir(60))));
	dot(rotate(60*i)*((1,0)));
  draw(rotate(60*i)*((0,0)--(1,0)--dir(60)));
}

for (i = 0; i <= 5; ++i) {
	dot(rotate(60*i)*((1,0) + dir(60)),red);
}
